mapper.update:
    	UPDATE	bbs_receipe_tb
    	SET		recipe_Title	=	#{recipe_Title},    			
          		recipe_Subject	=	#{recipe_Subject},
          		recipe_SubTitle	=	#{recipe_SubTitle},
    			recipe_Content	=	#{recipe_Content}
    	WHERE	intSeq			=	#{intSeq}

mapper.delete:
    	UPDATE	bbs_receipe_tb
    	SET		r_state		=	#{r_state}
    	WHERE	intSeq		=	#{seq}

mapper.update:
    	

	    	UPDATE	comment_tb
	    	SET		c_content	=	#{c_content}			
	    	WHERE	c_Seq		=	#{c_Seq}
	    	AND		c_Id		=	#{c_Id}

    	
	
	    	UPDATE	comment_tb
	    	SET		c_state	= 'N'
	    	WHERE	c_Seq	= #{seq}

mapper.select:
	    	SELECT		c_Seq
	    	FROM		comment_tb

			
				
		    		WHERE	b_Seq = #{seq}
				
				
		    		WHERE	c_parent_Seq = #{seq}
				
			

	    	AND			c_state = 'Y'

mapper.update:
    	
	
	    	UPDATE	comment_tb
	    	SET		c_state	= 'N'
	    	WHERE	c_Seq	= #{child}
